mapper.select:
SELECT
*
FROM
mytodo
WHERE
delete_date IS NULL

	AND item LIKE CONCAT('%', #{item}, '%')



	AND user_id = #{userId}


	AND priority = #{priority}


	AND complete_flag = #{completeFlag}


	AND deadline  <=  #{deadline}

mapper.update:
UPDATE
mytodo
SET
item = #{item}
,priority = #{priority}
,complete_flag = #{completeFlag}
,user_id = #{userId}
,deadline = #{deadline}
,update_date = CURRENT_TIMESTAMP
WHERE
todo_id = #{todoId}

UPDATE
mytodo
SET delete_date = CURRENT_TIMESTAMP, update_date = CURRENT_TIMESTAMP WHERE todo_id = #{todoId}

UPDATE
mytodo
SET complete_flag = 1, update_date = CURRENT_TIMESTAMP, complete_date = CURRENT_TIMESTAMP WHERE todo_id = #{todoId}
